mapper.select:
select body.exam_user_id, body.paper_id, body.state, body.description, body.technology_info_id as project, ( select data.dict_label from js_sys_dict_data data where data.dict_type = 'aa_car_body_skeleton' and data.dict_value = body.technology_info_id ) as projectName from aa_check_body_skeleton body
and body.paper_id = #{paperId}
and body.exam_user_id = #{examUserId}
